// This is the main application component that handles routing.
// All components are combined into this single file to ensure proper compilation and execution.

import React, { useState, useEffect } from 'react';

// API service handles all communication with the backend.
const apiService = {
  login: async (email, password) => {
    const response = await fetch(`http://127.0.0.1:8000/auth/login`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password }),
    });
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.detail || 'Login failed.');
    }
    return response.json();
  },

  signup: async (email, password) => {
    const response = await fetch(`http://127.0.0.1:8000/auth/register`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password }),
    });
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.detail || 'Registration failed.');
    }
    return response.json();
  },

  fetchProfile: async (token) => {
    const response = await fetch(`http://127.0.0.1:8000/auth/profile`, {
      method: 'GET',
      headers: { 'Authorization': `Bearer ${token}` },
    });
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.detail || 'Failed to fetch profile.');
    }
    return response.json();
  },

  fetchAllBlogs: async () => {
    const response = await fetch(`http://127.0.0.1:8000/blogs`);
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.detail || 'Failed to fetch blogs.');
    }
    return response.json();
  },

  fetchMyBlogs: async (token) => {
    const response = await fetch(`http://127.0.0.1:8000/blogs/my`, {
      method: 'GET',
      headers: { 'Authorization': `Bearer ${token}` },
    });
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.detail || 'Failed to fetch your blogs.');
    }
    return response.json();
  },

  createBlog: async (token, title, content) => {
    const response = await fetch(`http://127.0.0.1:8000/blogs/`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
      body: JSON.stringify({ title, content }),
    });
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.detail || 'Failed to create blog.');
    }
    return response.json();
  }
};

// Component for the navigation header.
const Header = ({ navigate, token, handleLogout }) => {
  return (
    <nav className="bg-white dark:bg-gray-800 shadow-md transition-colors duration-300">
      <div className="container mx-auto p-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-gray-900 dark:text-white cursor-pointer" onClick={() => navigate('/')}>MyBlog</h1>
        <div className="flex items-center space-x-4">
          <button className="text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white" onClick={() => navigate('/home')}>Home</button>
          {token ? (
            <>
              <button className="text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white" onClick={() => navigate('/profile')}>Profile</button>
              <button className="bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-full" onClick={() => navigate('/create-blog')}>Create Blog</button>
              <button className="text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white" onClick={handleLogout}>Logout</button>
            </>
          ) : (
            <>
              <button className="text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white" onClick={() => navigate('/login')}>Login</button>
              <button className="text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white" onClick={() => navigate('/signup')}>Sign Up</button>
            </>
          )}
        </div>
      </div>
    </nav>
  );
};

// Page that displays all blog posts.
const Home = () => {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const data = await apiService.fetchAllBlogs();
        setBlogs(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchBlogs();
  }, []);

  if (loading) return <p className="text-center mt-8">Loading blogs...</p>;
  if (error) return <p className="text-center mt-8 text-red-500">Error: {error}</p>;

  return (
    <div className="max-w-4xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center">All Blogs</h2>
      <div className="space-y-6">
        {blogs.length === 0 ? (
          <p className="text-center text-gray-500">No blogs published yet.</p>
        ) : (
          blogs.map(blog => (
            <div key={blog.id} className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6">
              <h3 className="text-xl font-bold">{blog.title}</h3>
              <p className="mt-2 text-gray-600 dark:text-gray-400">{blog.content}</p>
              <p className="text-sm mt-4 text-gray-500">By User ID: {blog.owner_id}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

// Page that provides the login form.
const Login = ({ navigate, setToken }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const res = await apiService.login(email, password);
      localStorage.setItem('token', res.access_token);
      setToken(res.access_token);
      setMessage('Login successful!');
      navigate('/profile');
    } catch (error) {
      setMessage(error.message || 'Login failed.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white dark:bg-gray-800 rounded-lg shadow-lg mt-10">
      <h2 className="text-2xl font-bold mb-4 text-center">Login</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          disabled={loading}
          className="w-full p-3 rounded-md border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          disabled={loading}
          className="w-full p-3 rounded-md border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          type="submit"
          disabled={loading}
          className={`w-full p-3 rounded-md font-semibold transition-colors duration-200 ${loading ? 'bg-gray-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700 text-white'}`}
        >
          {loading ? 'Logging in...' : 'Login'}
        </button>
      </form>
      {message && <p className={`mt-4 text-center ${message.includes('successful') ? 'text-green-500' : 'text-red-500'}`}>{message}</p>}
      <p className="mt-4 text-center">
        Don't have an account?{' '}
        <span className="text-blue-600 hover:underline cursor-pointer" onClick={() => navigate('/signup')}>
          Sign up here
        </span>
      </p>
    </div>
  );
};

// Page that provides the signup form.
const Signup = ({ navigate }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      await apiService.signup(email, password);
      setMessage('Registration successful! Please login.');
      setTimeout(() => navigate('/login'), 1500);
    } catch (error) {
      setMessage(error.message || 'Registration failed.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white dark:bg-gray-800 rounded-lg shadow-lg mt-10">
      <h2 className="text-2xl font-bold mb-4 text-center">Sign Up</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          disabled={loading}
          className="w-full p-3 rounded-md border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          disabled={loading}
          className="w-full p-3 rounded-md border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          type="submit"
          disabled={loading}
          className={`w-full p-3 rounded-md font-semibold transition-colors duration-200 ${loading ? 'bg-gray-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700 text-white'}`}
        >
          {loading ? 'Signing Up...' : 'Sign Up'}
        </button>
      </form>
      {message && <p className={`mt-4 text-center ${message.includes('successful') ? 'text-green-500' : 'text-red-500'}`}>{message}</p>}
      <p className="mt-4 text-center">
        Already have an account?{' '}
        <span className="text-blue-600 hover:underline cursor-pointer" onClick={() => navigate('/login')}>
          Login here
        </span>
      </p>
    </div>
  );
};

// Page that displays the user's profile and their own blog posts.
const Profile = ({ navigate, handleLogout }) => {
  const [profile, setProfile] = useState(null);
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    const fetchData = async () => {
      try {
        const [profileData, blogsData] = await Promise.all([
          apiService.fetchProfile(token),
          apiService.fetchMyBlogs(token)
        ]);
        setProfile(profileData);
        setBlogs(blogsData);
      } catch (err) {
        setError(err.message);
        handleLogout();
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [navigate, handleLogout]);

  if (loading) return <p className="text-center mt-8">Loading profile...</p>;
  if (error) return <p className="text-center mt-8 text-red-500">Error: {error}</p>;

  return (
    <div className="max-w-4xl mx-auto">
      <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6 sm:p-8 mb-8">
        <h2 className="text-3xl font-bold mb-2">My Profile</h2>
        {profile && (
          <div className="space-y-4">
            <p><strong>User ID:</strong> {profile.id}</p>
            <p><strong>Email:</strong> {profile.email}</p>
            <p><strong>Total Blogs:</strong> {profile.total_blogs}</p>
            <button
              onClick={handleLogout}
              className="bg-red-600 hover:bg-red-700 text-white font-semibold py-2 px-4 rounded-full transition-colors duration-200 shadow-md"
            >
              Logout
            </button>
          </div>
        )}
      </div>

      <h2 className="text-3xl font-bold mb-6 text-center">My Blogs</h2>
      <div className="space-y-6">
        {blogs.length === 0 ? (
          <p className="text-center text-gray-500">You haven't published any blogs yet.</p>
        ) : (
          blogs.map(blog => (
            <div key={blog.id} className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6">
              <h3 className="text-xl font-bold">{blog.title}</h3>
              <p className="mt-2 text-gray-600 dark:text-gray-400">{blog.content}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

// Page that provides a form to create a new blog post.
const CreateBlog = ({ navigate, token }) => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      await apiService.createBlog(token, title, content);
      setMessage('Blog post created successfully!');
      setTitle('');
      setContent('');
      setTimeout(() => navigate('/profile'), 1500);
    } catch (error) {
      setMessage(error.message || 'Failed to create blog post.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white dark:bg-gray-800 rounded-lg shadow-lg mt-10">
      <h2 className="text-2xl font-bold mb-4 text-center">Create a New Blog Post</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          placeholder="Blog Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
          disabled={loading}
          className="w-full p-3 rounded-md border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <textarea
          placeholder="Blog Content"
          rows="10"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          required
          disabled={loading}
          className="w-full p-3 rounded-md border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          type="submit"
          disabled={loading}
          className={`w-full p-3 rounded-md font-semibold transition-colors duration-200 ${loading ? 'bg-gray-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700 text-white'}`}
        >
          {loading ? 'Publishing...' : 'Publish Blog'}
        </button>
      </form>
      {message && <p className={`mt-4 text-center ${message.includes('successful') ? 'text-green-500' : 'text-red-500'}`}>{message}</p>}
    </div>
  );
};

// The main application component that renders everything.
export default function App() {
  const [page, setPage] = useState(window.location.pathname);
  const [token, setToken] = useState(localStorage.getItem('token'));

  useEffect(() => {
    const handlePopState = () => setPage(window.location.pathname);
    window.addEventListener('popstate', handlePopState);
    return () => window.removeEventListener('popstate', handlePopState);
  }, []);

  const navigate = (path) => {
    window.history.pushState({}, '', path);
    setPage(path);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setToken(null);
    navigate('/login');
  };

  const renderPage = () => {
    if (page === '/login') return <Login navigate={navigate} setToken={setToken} />;
    if (page === '/signup') return <Signup navigate={navigate} />;
    if (!token) return <Login navigate={navigate} setToken={setToken} />;

    switch (page) {
      case '/':
      case '/home':
        return <Home />;
      case '/profile':
        return <Profile navigate={navigate} handleLogout={handleLogout} />;
      case '/create-blog':
        return <CreateBlog navigate={navigate} token={token} />;
      default:
        return <Home />;
    }
  };

  return (
    <div className="bg-gray-100 dark:bg-gray-900 min-h-screen font-sans text-gray-800 dark:text-gray-200">
      <Header navigate={navigate} token={token} handleLogout={handleLogout} />
      <div className="container mx-auto p-4 sm:p-8">
        {renderPage()}
      </div>
    </div>
  );
}
