// src/App.js
// This is the main application component that handles routing.

import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import Home from './pages/Home';
import Login from './pages/Login';
import Signup from './pages/Signup';
import Profile from './pages/Profile';
import CreateBlog from './pages/CreateBlog';

export default function App() {
  const [page, setPage] = useState(window.location.pathname);
  const [token, setToken] = useState(localStorage.getItem('token'));

  useEffect(() => {
    // Listen for changes in the URL to handle navigation
    const handlePopState = () => setPage(window.location.pathname);
    window.addEventListener('popstate', handlePopState);
    return () => window.removeEventListener('popstate', handlePopState);
  }, []);

  const navigate = (path) => {
    window.history.pushState({}, '', path);
    setPage(path);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setToken(null);
    navigate('/login');
  };

  const renderPage = () => {
    if (page === '/login') return <Login navigate={navigate} setToken={setToken} />;
    if (page === '/signup') return <Signup navigate={navigate} />;
    if (!token) return <Login navigate={navigate} setToken={setToken} />;

    switch (page) {
      case '/':
      case '/home':
        return <Home />;
      case '/profile':
        return <Profile navigate={navigate} handleLogout={handleLogout} />;
      case '/create-blog':
        return <CreateBlog navigate={navigate} token={token} />;
      default:
        return <Home />;
    }
  };

  return (
    <div className="bg-gray-100 dark:bg-gray-900 min-h-screen font-sans text-gray-800 dark:text-gray-200">
      <Header navigate={navigate} token={token} handleLogout={handleLogout} />
      <div className="container mx-auto p-4 sm:p-8">
        {renderPage()}
      </div>
    </div>
  );
}
