// src/pages/Home.js
// This page displays all blog posts.

import React, { useState, useEffect } from 'react';
import apiService from '../services/apiService';

const Home = () => {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const data = await apiService.fetchAllBlogs();
        setBlogs(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchBlogs();
  }, []);

  if (loading) return <p className="text-center mt-8">Loading blogs...</p>;
  if (error) return <p className="text-center mt-8 text-red-500">Error: {error}</p>;

  return (
    <div className="max-w-4xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center">All Blogs</h2>
      <div className="space-y-6">
        {blogs.length === 0 ? (
          <p className="text-center text-gray-500">No blogs published yet.</p>
        ) : (
          blogs.map(blog => (
            <div key={blog.id} className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6">
              <h3 className="text-xl font-bold">{blog.title}</h3>
              <p className="mt-2 text-gray-600 dark:text-gray-400">{blog.content}</p>
              <p className="text-sm mt-4 text-gray-500">By User ID: {blog.owner_id}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Home;
