import React, { useState, useEffect } from 'react';
import {
  LogIn,
  User,
  ListPlus,
  LogOut,
  Home,
  Menu,
  X,
  PlusCircle,
  Eye,
  Trash2,
  Edit2
} from 'lucide-react';
import { createPortal } from 'react-dom';

// --- Constants and API Configuration ---
// The base URL for the FastAPI backend. Update this if your backend is running elsewhere.
const API_BASE_URL = 'http://127.0.0.1:8000';

// --- Global State Management (Simulated Context) ---
// This provides a way to share state like user data and login status across components.
const AppContext = React.createContext();

// --- Main App Component ---
export default function App() {
  const [currentPage, setCurrentPage] = useState('home');
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  // Effect to handle initial authentication check
  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    if (storedToken) {
      setToken(storedToken);
      fetchProfile(storedToken);
    }
  }, []);

  // Function to fetch user profile
  const fetchProfile = async (authToken) => {
    try {
      const response = await fetch(`${API_BASE_URL}/auth/profile`, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${authToken}`,
          'Content-Type': 'application/json',
        },
      });
      if (response.ok) {
        const data = await response.json();
        setUser(data);
      } else {
        localStorage.removeItem('token');
        setUser(null);
        setToken(null);
        setCurrentPage('login');
      }
    } catch (error) {
      console.error('Failed to fetch profile:', error);
      localStorage.removeItem('token');
      setUser(null);
      setToken(null);
      setCurrentPage('login');
    }
  };

  // Logout function
  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    setToken(null);
    setCurrentPage('home');
  };

  const pages = {
    'home': <HomePage setCurrentPage={setCurrentPage} />,
    'login': <LoginPage setCurrentPage={setCurrentPage} setUser={setUser} setToken={setToken} />,
    'register': <RegisterPage setCurrentPage={setCurrentPage} />,
    'profile': <ProfilePage user={user} handleLogout={handleLogout} />,
    'create-blog': <CreateBlogPage user={user} setCurrentPage={setCurrentPage} />,
    'my-blogs': <MyBlogsPage user={user} setCurrentPage={setCurrentPage} />,
    'all-blogs': <AllBlogsPage setCurrentPage={setCurrentPage} />,
  };

  // Function to render the correct page
  const renderPage = () => {
    if (user || currentPage === 'login' || currentPage === 'register' || currentPage === 'home' || currentPage === 'all-blogs') {
      return pages[currentPage] || pages['home'];
    }
    return pages['login'];
  };

  return (
    <AppContext.Provider value={{ user, token, handleLogout, setCurrentPage, API_BASE_URL }}>
      <div className="flex flex-col min-h-screen bg-gray-100 font-sans text-gray-800">
        <Header setIsSidebarOpen={setIsSidebarOpen} isSidebarOpen={isSidebarOpen} />
        <Sidebar isSidebarOpen={isSidebarOpen} setIsSidebarOpen={setIsSidebarOpen} />
        <main className="flex-grow p-4 md:p-8 overflow-y-auto">
          {renderPage()}
        </main>
      </div>
    </AppContext.Provider>
  );
}

// --- Layout Components ---

const Header = ({ setIsSidebarOpen, isSidebarOpen }) => {
  const { user, handleLogout, setCurrentPage } = React.useContext(AppContext);

  return (
    <header className="bg-white shadow-md p-4 flex items-center justify-between z-20 sticky top-0">
      <div className="flex items-center">
        <button
          onClick={() => setIsSidebarOpen(!isSidebarOpen)}
          className="text-gray-600 hover:text-gray-800 focus:outline-none focus:ring-2 focus:ring-indigo-500 rounded-md p-2 md:hidden"
        >
          {isSidebarOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
        <h1 className="text-xl md:text-2xl font-bold text-indigo-600 ml-4 cursor-pointer" onClick={() => setCurrentPage('home')}>
          My Awesome Blog
        </h1>
      </div>
      <nav className="hidden md:flex items-center space-x-4">
        <NavLink icon={<Home size={18} />} label="Home" onClick={() => setCurrentPage('home')} />
        <NavLink icon={<Eye size={18} />} label="All Blogs" onClick={() => setCurrentPage('all-blogs')} />
        {user && (
          <>
            <NavLink icon={<PlusCircle size={18} />} label="Create Blog" onClick={() => setCurrentPage('create-blog')} />
            <NavLink icon={<ListPlus size={18} />} label="My Blogs" onClick={() => setCurrentPage('my-blogs')} />
            <NavLink icon={<User size={18} />} label="Profile" onClick={() => setCurrentPage('profile')} />
            <NavLink icon={<LogOut size={18} />} label="Logout" onClick={handleLogout} />
          </>
        )}
        {!user && (
          <>
            <NavLink icon={<LogIn size={18} />} label="Login" onClick={() => setCurrentPage('login')} />
            <NavLink icon={<User size={18} />} label="Register" onClick={() => setCurrentPage('register')} />
          </>
        )}
      </nav>
      {user && (
        <div className="hidden md:block text-sm text-gray-600">
          Hello, {user.email}!
        </div>
      )}
    </header>
  );
};

const Sidebar = ({ isSidebarOpen, setIsSidebarOpen }) => {
  const { user, handleLogout, setCurrentPage } = React.useContext(AppContext);

  return createPortal(
    <div
      className={`fixed top-0 left-0 h-full w-64 bg-white shadow-xl transform transition-transform duration-300 z-30 ${
        isSidebarOpen ? 'translate-x-0' : '-translate-x-full'
      }`}
    >
      <div className="p-4 flex flex-col space-y-2">
        <button onClick={() => setIsSidebarOpen(false)} className="self-end text-gray-600 hover:text-gray-800">
          <X size={24} />
        </button>
        <SidebarLink icon={<Home size={20} />} label="Home" onClick={() => { setCurrentPage('home'); setIsSidebarOpen(false); }} />
        <SidebarLink icon={<Eye size={20} />} label="All Blogs" onClick={() => { setCurrentPage('all-blogs'); setIsSidebarOpen(false); }} />
        {user && (
          <>
            <SidebarLink icon={<PlusCircle size={20} />} label="Create Blog" onClick={() => { setCurrentPage('create-blog'); setIsSidebarOpen(false); }} />
            <SidebarLink icon={<ListPlus size={20} />} label="My Blogs" onClick={() => { setCurrentPage('my-blogs'); setIsSidebarOpen(false); }} />
            <SidebarLink icon={<User size={20} />} label="Profile" onClick={() => { setCurrentPage('profile'); setIsSidebarOpen(false); }} />
            <SidebarLink icon={<LogOut size={20} />} label="Logout" onClick={() => { handleLogout(); setIsSidebarOpen(false); }} />
          </>
        )}
        {!user && (
          <>
            <SidebarLink icon={<LogIn size={20} />} label="Login" onClick={() => { setCurrentPage('login'); setIsSidebarOpen(false); }} />
            <SidebarLink icon={<User size={20} />} label="Register" onClick={() => { setCurrentPage('register'); setIsSidebarOpen(false); }} />
          </>
        )}
      </div>
    </div>,
    document.body
  );
};

// --- Page Components ---

const HomePage = () => {
  const { user, setCurrentPage } = React.useContext(AppContext);
  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-lg shadow-xl">
      <div className="text-center">
        <h2 className="text-4xl font-extrabold text-gray-900 mb-4">
          Welcome to the Blog App
        </h2>
        <p className="text-xl text-gray-600 mb-8">
          Share your stories, ideas, and knowledge with the world.
        </p>
      </div>
      <div className="grid md:grid-cols-2 gap-8">
        <div className="bg-indigo-50 p-6 rounded-lg shadow-md">
          <h3 className="text-2xl font-semibold text-indigo-700 mb-3">
            Read All Blogs
          </h3>
          <p className="text-gray-700 mb-4">
            Explore a collection of blogs from all users. No login required!
          </p>
          <button
            onClick={() => setCurrentPage('all-blogs')}
            className="w-full px-6 py-3 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition-colors duration-300"
          >
            Browse Blogs
          </button>
        </div>
        <div className="bg-green-50 p-6 rounded-lg shadow-md">
          <h3 className="text-2xl font-semibold text-green-700 mb-3">
            Write Your Own
          </h3>
          <p className="text-gray-700 mb-4">
            Become a part of the community. Create, edit, and manage your own blogs.
          </p>
          {user ? (
            <button
              onClick={() => setCurrentPage('create-blog')}
              className="w-full px-6 py-3 bg-green-600 text-white rounded-md hover:bg-green-700 transition-colors duration-300"
            >
              Start Writing
            </button>
          ) : (
            <button
              onClick={() => setCurrentPage('register')}
              className="w-full px-6 py-3 bg-green-600 text-white rounded-md hover:bg-green-700 transition-colors duration-300"
            >
              Get Started
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

const LoginPage = () => {
  const { API_BASE_URL, setUser, setToken, setCurrentPage } = React.useContext(AppContext);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const response = await fetch(`${API_BASE_URL}/auth/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });
      const data = await response.json();

      if (response.ok) {
        localStorage.setItem('token', data.access_token);
        setToken(data.access_token);
        setMessage('Login successful! Redirecting...');
        // Fetch profile data after successful login
        const profileResponse = await fetch(`${API_BASE_URL}/auth/profile`, {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${data.access_token}`,
            'Content-Type': 'application/json',
          },
        });
        const profileData = await profileResponse.json();
        setUser(profileData);
        setTimeout(() => setCurrentPage('profile'), 1500);
      } else {
        setMessage(data.detail || 'Login failed');
      }
    } catch (error) {
      setMessage('Network error or server unavailable.');
      console.error('Login error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-[calc(100vh-8rem)]">
      <div className="w-full max-w-md p-8 bg-white rounded-lg shadow-lg">
        <h2 className="text-3xl font-bold text-center text-gray-900 mb-6">Login</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            disabled={loading}
            className="w-full px-4 py-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            disabled={loading}
            className="w-full px-4 py-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
          />
          <button
            type="submit"
            disabled={loading}
            className={`w-full py-3 rounded-md font-semibold transition-colors duration-300 ${loading ? 'bg-gray-400 cursor-not-allowed' : 'bg-indigo-600 text-white hover:bg-indigo-700'}`}
          >
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>
        {message && (
          <div className={`mt-4 p-3 rounded-md text-sm text-center ${message.includes('successful') ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`}>
            {message}
          </div>
        )}
        <div className="mt-6 text-center text-gray-600">
          Don't have an account?{' '}
          <button onClick={() => setCurrentPage('register')} className="text-indigo-600 hover:underline font-medium">
            Register here
          </button>
        </div>
      </div>
    </div>
  );
};

const RegisterPage = () => {
  const { API_BASE_URL, setCurrentPage } = React.useContext(AppContext);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const response = await fetch(`${API_BASE_URL}/auth/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });
      const data = await response.json();

      if (response.ok) {
        setMessage('Registration successful! Please login.');
        setTimeout(() => setCurrentPage('login'), 2000);
      } else {
        setMessage(data.detail || 'Registration failed');
      }
    } catch (error) {
      setMessage('Network error or server unavailable.');
      console.error('Registration error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-[calc(100vh-8rem)]">
      <div className="w-full max-w-md p-8 bg-white rounded-lg shadow-lg">
        <h2 className="text-3xl font-bold text-center text-gray-900 mb-6">Register</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            disabled={loading}
            className="w-full px-4 py-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            disabled={loading}
            className="w-full px-4 py-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
          />
          <button
            type="submit"
            disabled={loading}
            className={`w-full py-3 rounded-md font-semibold transition-colors duration-300 ${loading ? 'bg-gray-400 cursor-not-allowed' : 'bg-indigo-600 text-white hover:bg-indigo-700'}`}
          >
            {loading ? 'Registering...' : 'Register'}
          </button>
        </form>
        {message && (
          <div className={`mt-4 p-3 rounded-md text-sm text-center ${message.includes('successful') ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`}>
            {message}
          </div>
        )}
        <div className="mt-6 text-center text-gray-600">
          Already have an account?{' '}
          <button onClick={() => setCurrentPage('login')} className="text-indigo-600 hover:underline font-medium">
            Login here
          </button>
        </div>
      </div>
    </div>
  );
};

const ProfilePage = ({ user, handleLogout }) => {
  const { setCurrentPage } = React.useContext(AppContext);
  if (!user) {
    return (
      <div className="max-w-md mx-auto p-6 bg-white rounded-lg shadow-lg text-center">
        <h2 className="text-2xl font-bold text-red-600 mb-4">Error</h2>
        <p className="text-gray-700">User profile not found. Please log in.</p>
        <button
          onClick={() => setCurrentPage('login')}
          className="mt-4 px-6 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition-colors"
        >
          Go to Login
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-md mx-auto p-6 bg-white rounded-lg shadow-lg">
      <h2 className="text-3xl font-bold text-center text-gray-900 mb-6">User Profile</h2>
      <div className="space-y-4">
        <p className="flex items-center">
          <span className="font-semibold text-gray-700">Email:</span>
          <span className="ml-2 text-gray-600">{user.email}</span>
        </p>
        <p className="flex items-center">
          <span className="font-semibold text-gray-700">Total Blogs:</span>
          <span className="ml-2 text-gray-600">{user.total_blogs}</span>
        </p>
      </div>
      <div className="mt-8 flex flex-col space-y-4">
        <button
          onClick={() => setCurrentPage('create-blog')}
          className="w-full px-6 py-3 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition-colors"
        >
          Create New Blog
        </button>
        <button
          onClick={() => setCurrentPage('my-blogs')}
          className="w-full px-6 py-3 bg-green-600 text-white rounded-md hover:bg-green-700 transition-colors"
        >
          My Blogs
        </button>
        <button
          onClick={handleLogout}
          className="w-full px-6 py-3 bg-red-600 text-white rounded-md hover:bg-red-700 transition-colors"
        >
          Logout
        </button>
      </div>
    </div>
  );
};

const CreateBlogPage = () => {
  const { API_BASE_URL, token, setCurrentPage } = React.useContext(AppContext);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    try {
      const response = await fetch(`${API_BASE_URL}/blogs/`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ title, content }),
      });
      const data = await response.json();
      if (response.ok) {
        setMessage('Blog created successfully!');
        setTitle('');
        setContent('');
        setTimeout(() => setCurrentPage('my-blogs'), 1500);
      } else {
        setMessage(data.detail || 'Failed to create blog');
      }
    } catch (error) {
      setMessage('Network error or server unavailable.');
      console.error('Create blog error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white rounded-lg shadow-lg">
      <h2 className="text-3xl font-bold text-center text-gray-900 mb-6">Create New Blog</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label htmlFor="title" className="block text-sm font-medium text-gray-700">
            Title
          </label>
          <input
            id="title"
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            disabled={loading}
            className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
          />
        </div>
        <div>
          <label htmlFor="content" className="block text-sm font-medium text-gray-700">
            Content
          </label>
          <textarea
            id="content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            required
            disabled={loading}
            rows="10"
            className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
          ></textarea>
        </div>
        <button
          type="submit"
          disabled={loading}
          className={`w-full py-3 rounded-md font-semibold transition-colors duration-300 ${loading ? 'bg-gray-400 cursor-not-allowed' : 'bg-indigo-600 text-white hover:bg-indigo-700'}`}
        >
          {loading ? 'Creating...' : 'Create Blog'}
        </button>
      </form>
      {message && (
        <div className={`mt-4 p-3 rounded-md text-sm text-center ${message.includes('successfully') ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`}>
          {message}
        </div>
      )}
    </div>
  );
};

const MyBlogsPage = () => {
  const { API_BASE_URL, token, setCurrentPage } = React.useContext(AppContext);
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchMyBlogs();
  }, []);

  const fetchMyBlogs = async () => {
    try {
      if (!token) {
        setError("You are not logged in.");
        setLoading(false);
        return;
      }
      const response = await fetch(`${API_BASE_URL}/blogs/my`, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });
      const data = await response.json();
      if (response.ok) {
        setBlogs(data);
      } else {
        setError(data.detail || 'Failed to fetch your blogs.');
      }
    } catch (error) {
      setError('Network error or server unavailable.');
      console.error('Fetch my blogs error:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="text-center p-6">Loading blogs...</div>;
  if (error) return <div className="text-center p-6 text-red-600">{error}</div>;

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-lg shadow-lg">
      <h2 className="text-3xl font-bold text-center text-gray-900 mb-6">My Blogs</h2>
      {blogs.length === 0 ? (
        <div className="text-center text-gray-500">
          <p className="text-lg">You haven't written any blogs yet.</p>
          <button
            onClick={() => setCurrentPage('create-blog')}
            className="mt-4 px-6 py-3 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition-colors"
          >
            Create Your First Blog
          </button>
        </div>
      ) : (
        <ul className="space-y-4">
          {blogs.map((blog) => (
            <li key={blog.id} className="p-4 bg-gray-50 rounded-lg shadow-md">
              <h3 className="text-xl font-semibold text-gray-900">{blog.title}</h3>
              <p className="text-gray-600 mt-2 line-clamp-3">{blog.content}</p>
              {/* Add edit and delete buttons in a real app */}
              <div className="mt-4 flex space-x-2">
                <button className="flex items-center text-sm text-indigo-600 hover:underline">
                  <Edit2 size={16} className="mr-1" /> Edit
                </button>
                <button className="flex items-center text-sm text-red-600 hover:underline">
                  <Trash2 size={16} className="mr-1" /> Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

const AllBlogsPage = () => {
