  }, []);

  const fetchAllBlogs = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/blogs/`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const data = await response.json();
      if (response.ok) {
        setBlogs(data);
      } else {
