import React, { useState } from "react";
import { signUp } from "../services/user-service";

function Signup() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    about: "",
    profile_pic: "",
  });

  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    console.log("üîç Signup form submitted");
    console.log("Form data:", { ...formData, password: "[HIDDEN]" });

    try {
      // Basic validation
      if (!formData.name || !formData.email || !formData.password) {
        throw new Error("Please fill in all required fields");
      }

      if (!formData.email.includes("@")) {
        throw new Error("Please enter a valid email address");
      }

      if (formData.password.length < 6) {
        throw new Error("Password must be at least 6 characters long");
      }

      console.log("üì§ Calling signup service...");
      const res = await signUp(formData);

      console.log("‚úÖ Signup successful:", res);
      setMessage("Signup successful! Redirecting to login...");

      // Clear form
      setFormData({
        name: "",
        email: "",
        password: "",
        about: "",
        profile_pic: "",
      });

      // Redirect to login after successful signup
      setTimeout(() => {
        window.location.href = "/login";
      }, 2000);

    } catch (error) {
      console.error("‚ùå Signup failed:", error);
      setMessage(error.message || "Signup failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: "400px", margin: "auto", padding: "20px" }}>
      <h2>Create Account</h2>

      {/* Debug info */}
      <div style={{ 
        backgroundColor: "#f8f9fa", 
        padding: "10px", 
        marginBottom: "20px", 
        borderRadius: "4px",
        fontSize: "12px",
        color: "#666"
      }}>
        <div><strong>Debug Info:</strong></div>
        <div>Backend URL: http://127.0.0.1:8000</div>
        <div>Form valid: {formData.name && formData.email && formData.password ? "Yes" : "No"}</div>
      </div>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Full Name *"
          value={formData.name}
          onChange={handleChange}
          required
          disabled={loading}
          style={{ 
            width: "100%", 
            padding: "10px", 
            margin: "10px 0", 
            borderRadius: "4px", 
            border: "1px solid #ccc",
            backgroundColor: loading ? "#f5f5f5" : "white"
          }}
        />

        <input
          type="email"
          name="email"
          placeholder="Email Address *"
          value={formData.email}
          onChange={handleChange}
          required
          disabled={loading}
          style={{ 
            width: "100%", 
            padding: "10px", 
            margin: "10px 0", 
            borderRadius: "4px", 
            border: "1px solid #ccc",
            backgroundColor: loading ? "#f5f5f5" : "white"
          }}
        />

        <input
          type="password"
          name="password"
          placeholder="Password (min 6 chars) *"
          value={formData.password}
          onChange={handleChange}
          required
          disabled={loading}
          minLength="6"
          style={{ 
            width: "100%", 
            padding: "10px", 
            margin: "10px 0", 
            borderRadius: "4px", 
            border: "1px solid #ccc",
            backgroundColor: loading ? "#f5f5f5" : "white"
          }}
        />

        <textarea
          name="about"
          placeholder="About you (optional)"
          value={formData.about}
          onChange={handleChange}
          disabled={loading}
          style={{ 
            width: "100%", 
            padding: "10px", 
            margin: "10px 0", 
            borderRadius: "4px", 
            border: "1px solid #ccc",
            minHeight: "80px",
            backgroundColor: loading ? "#f5f5f5" : "white",
            resize: "vertical"
          }}
        />

        <input
          type="url"
          name="profile_pic"
          placeholder="Profile Photo URL (optional)"
          value={formData.profile_pic}
          onChange={handleChange}
          disabled={loading}
          style={{ 
            width: "100%", 
            padding: "10px", 
            margin: "10px 0", 
            borderRadius: "4px", 
            border: "1px solid #ccc",
            backgroundColor: loading ? "#f5f5f5" : "white"
          }}
        />

        <button 
          type="submit"
          disabled={loading}
          style={{
            width: "100%",
            padding: "10px",
            backgroundColor: loading ? "#6c757d" : "#28a745",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: loading ? "not-allowed" : "pointer"
          }}
        >
          {loading ? "Creating Account..." : "Create Account"}
        </button>
      </form>

      {message && (
        <p style={{ 
          color: message.includes("successful") ? "green" : "red",
          marginTop: "15px",
          padding: "10px",
          backgroundColor: message.includes("successful") ? "#d4edda" : "#f8d7da",
          borderRadius: "4px"
        }}>
          {message}
        </p>
      )}

      {/* Navigation links */}
      <div style={{ marginTop: "20px", textAlign: "center" }}>
        <p>
          Already have an account? <a href="/login">Login here</a>
        </p>
      </div>
    </div>
  );
}

export default Signup;
