import React, { useState } from "react";
import { login } from "../services/user-service";

function Login() {
  const [email, setEmail] = useState("");  // Changed back to email
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await login(email, password);  // Use email for login
      localStorage.setItem("token", res.access_token);
      setMessage("Login successful!");
      console.log(res);
      // Redirect to profile or dashboard
      window.location.href = "/profile";
    } catch (error) {
      setMessage(error.message);
    }
  };

  return (
    <div style={{ maxWidth: "400px", margin: "auto", padding: "20px" }}>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"  // Changed back to email type
          placeholder="Email"  // Changed placeholder back
          value={email}  // Changed back to email
          onChange={(e) => setEmail(e.target.value)}  // Changed back to setEmail
          required
          style={{ 
            width: "100%", 
            padding: "10px", 
            margin: "10px 0", 
            borderRadius: "4px", 
            border: "1px solid #ccc" 
          }}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={{ 
            width: "100%", 
            padding: "10px", 
            margin: "10px 0", 
            borderRadius: "4px", 
            border: "1px solid #ccc" 
          }}
        />
        <button 
          type="submit"
          style={{
            width: "100%",
            padding: "10px",
            backgroundColor: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer"
          }}
        >
          Login
        </button>
      </form>
      {message && <p style={{ color: message.includes("successful") ? "green" : "red" }}>{message}</p>}
    </div>
  );
}

export default Login;
