// This is the main application component that handles routing and theme switching.
// All components are combined into this single file to ensure proper compilation and execution.

import React, { useState, useEffect } from 'react';

// API service handles all communication with the backend.
const apiService = {
  login: async (email, password) => {
    const response = await fetch(`http://127.0.0.1:8000/auth/login`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password }),
    });
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.detail || 'Login failed.');
    }
    return response.json();
  },

  signup: async (email, password) => {
    const response = await fetch(`http://127.0.0.1:8000/auth/register`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password }),
    });
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.detail || 'Registration failed.');
    }
    return response.json();
  },

  fetchProfile: async (token) => {
    const response = await fetch(`http://127.0.0.1:8000/auth/profile`, {
      method: 'GET',
      headers: { 'Authorization': `Bearer ${token}` },
    });
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.detail || 'Failed to fetch profile.');
    }
    return response.json();
  },

  fetchAllBlogs: async () => {
    const response = await fetch(`http://127.0.0.1:8000/blogs`);
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.detail || 'Failed to fetch blogs.');
    }
    return response.json();
  },

  fetchMyBlogs: async (token) => {
    const response = await fetch(`http://127.0.0.1:8000/blogs/my`, {
      method: 'GET',
      headers: { 'Authorization': `Bearer ${token}` },
    });
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.detail || 'Failed to fetch your blogs.');
    }
    return response.json();
  },

  createBlog: async (token, title, content) => {
    const response = await fetch(`http://127.0.0.1:8000/blogs/`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
      body: JSON.stringify({ title, content }),
    });
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.detail || 'Failed to create blog.');
    }
    return response.json();
  }
};

// Component for the navigation header.
const Header = ({ navigate, token, handleLogout, theme, toggleTheme }) => {
  const themeIcon = theme === 'dark' ? (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-yellow-400" viewBox="0 0 24 24" fill="currentColor">
      <path d="M12 2.25a.75.75 0 01.75.75v2.25a.75.75 0 01-1.5 0V3a.75.75 0 01.75-.75zM7.5 12a4.5 4.5 0 119 0 4.5 4.5 0 01-9 0zM18.894 6.106a.75.75 0 00-1.06-1.06l-1.59 1.59a.75.75 0 101.06 1.06l1.59-1.59zM21.75 12a.75.75 0 01-.75.75h-2.25a.75.75 0 010-1.5h2.25a.75.75 0 01.75.75zM17.25 18.894a.75.75 0 001.06 1.06l1.59-1.59a.75.75 0 10-1.06-1.06l-1.59 1.59zM12 18.75a.75.75 0 01-.75-.75v-2.25a.75.75 0 011.5 0v2.25a.75.75 0 01-.75.75zM4.59 17.25a.75.75 0 00-1.06 1.06l1.59 1.59a.75.75 0 101.06-1.06l-1.59-1.59zM2.25 12a.75.75 0 01.75-.75h2.25a.75.75 0 010 1.5H3a.75.75 0 01-.75-.75zM4.59 6.106a.75.75 0 001.06-1.06l-1.59-1.59a.75.75 0 10-1.06 1.06l1.59 1.59z" />
    </svg>
  ) : (
    <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-gray-500" viewBox="0 0 24 24" fill="currentColor">
      <path fillRule="evenodd" d="M9.546 1.516a.75.75 0 01.445.962l-.65 1.566c-.638.165-1.144.576-1.463 1.129l-1.566.65a.75.75 0 01-.962-.445l-1.621-3.905a.75.75 0 01.445-.962l3.905-1.621a.75.75 0 01.962.445zM13.25 7.5a.75.75 0 00-1.5 0v2.25a.75.75 0 001.5 0V7.5zM17.159 4.376a.75.75 0 00-.962.445l-1.566 3.765a4.502 4.502 0 01-1.129 1.463l-3.765 1.566a.75.75 0 00-.445.962l.857 2.062a.75.75 0 00.962.445l1.566-.65a.75.75 0 00-.445-.962l-1.566-.65a.75.75 0 00-.962.445l-.857 2.062a.75.75 0 00.962.445l3.905-1.621a.75.75 0 00.445-.962l-1.621-3.905a.75.75 0 00-.962-.445l-1.566.65a.75.75 0 00-.445.962l1.566 3.765a4.502 4.502 0 011.463 1.129l3.765 1.566a.75.75 0 00.962-.445l.857-2.062a.75.75 0 00-.962-.445l-1.566.65a.75.75 0 00-.445-.962l-3.765-1.566a4.502 4.502 0 01-1.129-1.463l-1.566-3.765a.75.75 0 00-.962-.445l-2.062.857a.75.75 0 00-.445.962l1.566 3.765a4.502 4.502 0 011.129 1.463l3.765 1.566a.75.75 0 00.962-.445l1.621-3.905a.75.75 0 00-.445-.962l-3.905-1.621a.75.75 0 00-.962.445zM12 21.75a.75.75 0 01-.75-.75v-2.25a.75.75 0 011.5 0v2.25a.75.75 0 01-.75.75z" clipRule="evenodd" />
    </svg>
  );

  return (
    <nav className="bg-white dark:bg-gray-900 border-b border-gray-200 dark:border-gray-700 transition-colors duration-300">
      <div className="container mx-auto p-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-gray-900 dark:text-white cursor-pointer" onClick={() => navigate('/')}>MyBlog</h1>
        <div className="flex items-center space-x-4">
          <button className="text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white transition-colors duration-200" onClick={() => navigate('/home')}>Home</button>
          {token ? (
            <>
              <button className="text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white transition-colors duration-200" onClick={() => navigate('/profile')}>Profile</button>
              <button className="bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-full transition-colors duration-200" onClick={() => navigate('/create-blog')}>Create Blog</button>
              <button className="text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white transition-colors duration-200" onClick={handleLogout}>Logout</button>
            </>
          ) : (
            <>
              <button className="text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white transition-colors duration-200" onClick={() => navigate('/login')}>Login</button>
              <button className="text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white transition-colors duration-200" onClick={() => navigate('/signup')}>Sign Up</button>
            </>
          )}
          <button onClick={toggleTheme} className="p-2 rounded-full hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors duration-200">
            {themeIcon}
          </button>
        </div>
      </div>
    </nav>
  );
};

// Page that displays all blog posts.
const Home = () => {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const data = await apiService.fetchAllBlogs();
        setBlogs(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchBlogs();
  }, []);

  if (loading) return <p className="text-center mt-8 text-gray-800 dark:text-gray-200">Loading blogs...</p>;
  if (error) return <p className="text-center mt-8 text-red-500">Error: {error}</p>;

  return (
    <div className="max-w-4xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center text-gray-900 dark:text-white">All Blogs</h2>
      <div className="space-y-6">
        {blogs.length === 0 ? (
          <p className="text-center text-gray-500">No blogs published yet.</p>
        ) : (
          blogs.map(blog => (
            <div key={blog.id} className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6">
              <h3 className="text-xl font-bold text-gray-900 dark:text-white">{blog.title}</h3>
              <p className="mt-2 text-gray-600 dark:text-gray-400">{blog.content}</p>
              <p className="text-sm mt-4 text-gray-500">By User ID: {blog.owner_id}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

// Page that provides the login form.
const Login = ({ navigate, setToken }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      const res = await apiService.login(email, password);
      localStorage.setItem('token', res.access_token);
      setToken(res.access_token);
      setMessage('Login successful!');
      navigate('/profile');
    } catch (error) {
      setMessage(error.message || 'Login failed.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white dark:bg-gray-800 rounded-lg shadow-lg mt-10">
      <h2 className="text-2xl font-bold mb-4 text-center text-gray-900 dark:text-white">Login</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          disabled={loading}
          className="w-full p-3 rounded-md border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          disabled={loading}
          className="w-full p-3 rounded-md border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          type="submit"
          disabled={loading}
          className={`w-full p-3 rounded-md font-semibold transition-colors duration-200 ${loading ? 'bg-gray-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700 text-white'}`}
        >
          {loading ? 'Logging in...' : 'Login'}
        </button>
      </form>
      {message && <p className={`mt-4 text-center ${message.includes('successful') ? 'text-green-500' : 'text-red-500'}`}>{message}</p>}
      <p className="mt-4 text-center text-gray-600 dark:text-gray-400">
        Don't have an account?{' '}
        <span className="text-blue-600 hover:underline cursor-pointer" onClick={() => navigate('/signup')}>
          Sign up here
        </span>
      </p>
    </div>
  );
};

