// Page that displays the user's profile and their own blog posts.
const Profile = ({ navigate, handleLogout }) => {
  const [profile, setProfile] = useState(null);
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    const fetchData = async () => {
      try {
        const [profileData, blogsData] = await Promise.all([
          apiService.fetchProfile(token),
          apiService.fetchMyBlogs(token)
        ]);
        setProfile(profileData);
        setBlogs(blogsData);
      } catch (err) {
        setError(err.message);
        handleLogout();
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [navigate, handleLogout]);

  if (loading) return <p className="text-center mt-8 text-gray-800 dark:text-gray-200">Loading profile...</p>;
  if (error) return <p className="text-center mt-8 text-red-500">Error: {error}</p>;

  return (
    <div className="max-w-4xl mx-auto">
      <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-6 sm:p-8 mb-8">
        <h2 className="text-3xl font-bold mb-2 text-gray-900 dark:text-white">My Profile</h2>
        {profile && (
          <div className="space-y-4">
            <p className="text-gray-600 dark:text-gray-400"><strong>User ID:</strong> {profile.id}</p>
            <p className="text-gray-600 dark:text-gray-400"><strong>Email:</strong> {profile.email}</p>
            <p className="text-gray-600 dark:text-gray-400"><strong>Total Blogs:</strong> {profile.total_blogs}</p>
            <button
              onClick={handleLogout}
              className="bg-red-600 hover:bg-red-700 text-white font-semibold py-2 px-4 rounded-full transition-colors duration-200 shadow-md"
            >
              Logout
            </button>
          </div>
        )}
      </div>

      <h2 className="text-3xl font-bold mb-6 text-center text-gray-900 dark:text-white">My Blogs</h2>
      <div className="space-y-6">
        {blogs.length === 0 ? (
          <p className="text-center text-gray-500">You haven't published any blogs yet.</p>
        ) : (
          blogs.map(blog => (
            <div key={blog.id} className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6">
              <h3 className="text-xl font-bold text-gray-900 dark:text-white">{blog.title}</h3>
              <p className="mt-2 text-gray-600 dark:text-gray-400">{blog.content}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

