// The main application component that renders everything.
export default function App() {
  const [page, setPage] = useState(window.location.pathname);
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [theme, setTheme] = useState(localStorage.getItem('theme') || 'light');

  useEffect(() => {
    // Listen for changes in the URL to handle navigation
    const handlePopState = () => setPage(window.location.pathname);
    window.addEventListener('popstate', handlePopState);
    return () => window.removeEventListener('popstate', handlePopState);
  }, []);

  useEffect(() => {
    // Apply theme class to the body element
    if (theme === 'dark') {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
    // Save theme preference to localStorage
    localStorage.setItem('theme', theme);
  }, [theme]);

  const navigate = (path) => {
    window.history.pushState({}, '', path);
    setPage(path);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setToken(null);
    navigate('/login');
  };

  const toggleTheme = () => {
    setTheme(currentTheme => (currentTheme === 'light' ? 'dark' : 'light'));
  };

  const renderPage = () => {
    if (page === '/login') return <Login navigate={navigate} setToken={setToken} />;
    if (page === '/signup') return <Signup navigate={navigate} />;
    if (!token) return <Login navigate={navigate} setToken={setToken} />;

    switch (page) {
      case '/':
      case '/home':
        return <Home />;
      case '/profile':
        return <Profile navigate={navigate} handleLogout={handleLogout} />;
      case '/create-blog':
        return <CreateBlog navigate={navigate} token={token} />;
      default:
        return <Home />;
    }
  };

  return (
    <div className="bg-gray-100 dark:bg-gray-950 min-h-screen font-sans text-gray-800 dark:text-gray-200 transition-colors duration-300">
      <Header navigate={navigate} token={token} handleLogout={handleLogout} theme={theme} toggleTheme={toggleTheme} />
      <div className="container mx-auto p-4 sm:p-8">
        {renderPage()}
      </div>
    </div>
  );
}
