import React, { useState, useEffect } from "react";
import { login, testConnection } from "../services/user-service";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  // Test backend connection on component mount
  useEffect(() => {
    testBackendConnection();
  }, []);

  const testBackendConnection = async () => {
    try {
      await testConnection();
      console.log("‚úÖ Backend connection verified");
    } catch (error) {
      console.error("‚ùå Backend connection failed:", error);
      setMessage("Cannot connect to server. Please check if the backend is running.");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    console.log("üîç Login form submitted");
    console.log("Email:", email);
    console.log("Password length:", password.length);

    try {
      // Validate inputs
      if (!email || !password) {
        throw new Error("Please fill in all fields");
      }

      if (!email.includes("@")) {
        throw new Error("Please enter a valid email address");
      }

      console.log("üì§ Calling login service...");
      const res = await login(email, password);

      console.log("‚úÖ Login service response:", res);

      if (res.access_token) {
        localStorage.setItem("token", res.access_token);
        setMessage("Login successful! Redirecting...");

        console.log("üé´ Token saved to localStorage");
        console.log("üîÑ Redirecting to profile...");

        // Small delay to show success message
        setTimeout(() => {
          window.location.href = "/profile";
        }, 1500);
      } else {
        throw new Error("No access token received");
      }

    } catch (error) {
      console.error("‚ùå Login failed:", error);
      setMessage(error.message || "Login failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: "400px", margin: "auto", padding: "20px" }}>
      <h2>Login</h2>

      {/* Debug info */}
      <div style={{ 
        backgroundColor: "#f8f9fa", 
        padding: "10px", 
        marginBottom: "20px", 
        borderRadius: "4px",
        fontSize: "12px",
        color: "#666"
      }}>
        <div><strong>Debug Info:</strong></div>
        <div>Backend URL: http://127.0.0.1:8000</div>
        <div>Token in storage: {localStorage.getItem("token") ? "Yes" : "No"}</div>
      </div>

      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          disabled={loading}
          style={{ 
            width: "100%", 
            padding: "10px", 
            margin: "10px 0", 
            borderRadius: "4px", 
            border: "1px solid #ccc",
            backgroundColor: loading ? "#f5f5f5" : "white"
          }}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          disabled={loading}
          style={{ 
            width: "100%", 
            padding: "10px", 
            margin: "10px 0", 
            borderRadius: "4px", 
            border: "1px solid #ccc",
            backgroundColor: loading ? "#f5f5f5" : "white"
          }}
        />
        <button 
          type="submit"
          disabled={loading}
          style={{
            width: "100%",
            padding: "10px",
            backgroundColor: loading ? "#6c757d" : "#007bff",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: loading ? "not-allowed" : "pointer"
          }}
        >
          {loading ? "Logging in..." : "Login"}
        </button>
      </form>

      {message && (
        <p style={{ 
          color: message.includes("successful") ? "green" : "red",
          marginTop: "15px",
          padding: "10px",
          backgroundColor: message.includes("successful") ? "#d4edda" : "#f8d7da",
          borderRadius: "4px"
        }}>
          {message}
        </p>
      )}

      {/* Test buttons for debugging */}
      <div style={{ marginTop: "20px", borderTop: "1px solid #eee", paddingTop: "20px" }}>
        <h4>Debug Tools:</h4>
        <button 
          onClick={testBackendConnection}
          style={{
            padding: "5px 10px",
            marginRight: "10px",
            backgroundColor: "#28a745",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer"
          }}
        >
          Test Backend Connection
        </button>

        <button 
          onClick={() => {
            localStorage.removeItem("token");
            setMessage("Token cleared from localStorage");
          }}
          style={{
            padding: "5px 10px",
            backgroundColor: "#dc3545",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer"
          }}
        >
          Clear Token
        </button>
      </div>

      {/* Navigation links */}
      <div style={{ marginTop: "20px", textAlign: "center" }}>
        <p>
          Don't have an account? <a href="/signup">Sign up here</a>
        </p>
      </div>
    </div>
  );
}

export default Login;
