import React, { useState, useEffect } from 'react';

// Mock service functions to make the app runnable in a self-contained environment
const MOCK_API_BASE_URL = 'http://127.0.0.1:8000';

const login = async (email, password) => {
  console.log(`Attempting to log in with email: ${email}`);
  try {
    const response = await fetch(`${MOCK_API_BASE_URL}/auth/login`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email, password }),
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.detail || 'Login failed');
    }

    const data = await response.json();
    return data;
  } catch (error) {
    console.error("Login API call failed:", error);
    throw error;
  }
};

const getProfile = async (token) => {
  console.log("Fetching user profile...");
  try {
    const response = await fetch(`${MOCK_API_BASE_URL}/auth/profile`, {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.detail || 'Failed to fetch profile');
    }

    const data = await response.json();
    return data;
  } catch (error) {
    console.error("Profile API call failed:", error);
    throw error;
  }
};

const createBlog = async (title, content, token) => {
  console.log(`Creating blog with title: ${title}`);
  try {
    const response = await fetch(`${MOCK_API_BASE_URL}/blogs/`, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ title, content }),
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.detail || 'Failed to create blog');
    }

    const data = await response.json();
    return data;
  } catch (error) {
    console.error("Create blog API call failed:", error);
    throw error;
  }
};

const getMyBlogs = async (token) => {
  console.log("Fetching my blogs...");
  try {
    const response = await fetch(`${MOCK_API_BASE_URL}/blogs/my`, {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.detail || 'Failed to fetch my blogs');
    }

    const data = await response.json();
    return data;
  } catch (error) {
    console.error("Get my blogs API call failed:", error);
    throw error;
  }
};

// Main application component that handles navigation
const App = () => {
  // Use state to manage the current "page" or view, avoiding browser history manipulation.
  const [currentPage, setCurrentPage] = useState('home');
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [message, setMessage] = useState('');

  // A component to display a simple error or success message
  const MessageDisplay = ({ msg, type }) => {
    if (!msg) return null;
    const color = type === 'error' ? 'red' : 'green';
    const bgColor = type === 'error' ? 'bg-red-100' : 'bg-green-100';
    return (
      <div className={`mt-4 p-4 rounded-lg shadow-sm text-sm ${bgColor} text-${color}-800`}>
        {msg}
      </div>
    );
  };

  // Component for the login form
  const Login = ({ onLoginSuccess }) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
      e.preventDefault();
      setLoading(true);
      setMessage('');

      try {
        if (!email || !password) {
          setMessage("Please fill in all fields.");
          return;
        }

        const res = await login(email, password);

        if (res.access_token) {
          localStorage.setItem("token", res.access_token);
          setToken(res.access_token); // Update the main app's token state
          setMessage("Login successful!");
          onLoginSuccess();
        } else {
          setMessage("Login failed: No access token received.");
        }
      } catch (error) {
        setMessage(error.message || "Login failed due to a network error.");
      } finally {
        setLoading(false);
      }
    };

    return (
      <div className="flex flex-col items-center justify-center p-8 bg-white rounded-lg shadow-2xl w-full max-w-sm mx-auto my-12">
        <h2 className="text-3xl font-bold text-gray-800 mb-6">Login</h2>
        <form onSubmit={handleSubmit} className="w-full space-y-4">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            disabled={loading}
            className="w-full px-4 py-3 rounded-md border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-200"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            disabled={loading}
            className="w-full px-4 py-3 rounded-md border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500 transition duration-200"
          />
          <button
            type="submit"
            disabled={loading}
            className="w-full py-3 rounded-md text-white font-semibold transition-all duration-300 ease-in-out transform hover:scale-105"
            style={{ backgroundColor: loading ? '#6c757d' : '#007bff' }}
          >
            {loading ? "Logging in..." : "Login"}
          </button>
        </form>
        <MessageDisplay msg={message} type={message.includes('successful') ? 'success' : 'error'} />
      </div>
    );
  };

  // Component for the user profile page
  const Profile = ({ onLogout, onCreateBlog, onMyBlogs, onHome }) => {
    const [profile, setProfile] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    useEffect(() => {
      // Use the token from the main App state
      const fetchProfile = async () => {
        try {
          if (!token) {
            setError("No token found. Please login.");
            setLoading(false);
            return;
          }
          const data = await getProfile(token);
          setProfile(data);
        } catch (error) {
          setError(error.message || "Failed to fetch profile.");
        } finally {
          setLoading(false);
        }
      };

      fetchProfile();
    }, [token]);

    const handleLogout = () => {
      localStorage.removeItem("token");
      onLogout(); // Call the logout function passed from App
    };

    if (loading) {
      return (
        <div className="flex justify-center items-center h-screen">
          <p className="text-lg text-gray-600">Loading profile...</p>
        </div>
      );
    }

    if (error) {
      return (
        <div className="text-center p-8 bg-white rounded-lg shadow-xl m-12">
          <p className="text-xl font-semibold text-red-600">Error</p>
          <p className="mt-4 text-gray-700">{error}</p>
          <button onClick={onHome} className="mt-6 px-6 py-2 bg-blue-500 text-white rounded-lg shadow-md hover:bg-blue-600 transition duration-300">
            Go to Home
          </button>
        </div>
      );
    }

    return (
      <div className="flex flex-col items-center p-8 bg-white rounded-lg shadow-2xl w-full max-w-2xl mx-auto my-12 space-y-8">
        <h1 className="text-4xl font-extrabold text-gray-900 mb-2">My Profile</h1>
        {profile && (
          <div className="flex flex-col items-center text-center">
            <div className="w-24 h-24 bg-gray-200 rounded-full flex items-center justify-center text-gray-600 text-5xl font-bold">
              {profile.email.charAt(0).toUpperCase()}
            </div>
            <p className="text-2xl font-semibold text-gray-800 mt-4">Email: {profile.email}</p>
            <p className="text-lg text-gray-600">Total Blogs: {profile.total_blogs}</p>
          </div>
        )}
        <div className="flex flex-col md:flex-row space-y-4 md:space-y-0 md:space-x-4">
          <button
            onClick={onCreateBlog}
            className="px-6 py-3 rounded-lg text-white font-semibold transition-all duration-300 ease-in-out transform hover:scale-105"
            style={{ backgroundColor: '#007bff' }}
          >
            Create New Blog
          </button>
          <button
            onClick={onMyBlogs}
            className="px-6 py-3 rounded-lg text-white font-semibold transition-all duration-300 ease-in-out transform hover:scale-105"
            style={{ backgroundColor: '#28a745' }}
          >
            My Blogs
          </button>
          <button
            onClick={onHome}
            className="px-6 py-3 rounded-lg text-white font-semibold transition-all duration-300 ease-in-out transform hover:scale-105"
            style={{ backgroundColor: '#6c757d' }}
          >
            Home
          </button>
          <button
            onClick={handleLogout}
            className="px-6 py-3 rounded-lg text-white font-semibold transition-all duration-300 ease-in-out transform hover:scale-105"
            style={{ backgroundColor: '#dc3545' }}
          >
            Logout
          </button>
        </div>
      </div>
    );
  };

  // Component for the home page
  const Home = ({ onNavigate }) => (
    <div className="flex flex-col items-center justify-center h-full p-8 bg-gray-100">
      <h1 className="text-4xl font-extrabold text-gray-900 mb-4">Welcome to the Blog App</h1>
      <p className="text-lg text-gray-600 mb-8 text-center max-w-md">
        Please log in or register to create and manage your blogs.
      </p>
      <div className="flex space-x-4">
        <button
          onClick={() => onNavigate('login')}
          className="px-8 py-3 bg-blue-600 text-white font-semibold rounded-lg shadow-md hover:bg-blue-700 transition duration-300 transform hover:scale-105"
        >
          Login
        </button>
        <button
          onClick={() => onNavigate('profile')}
          className="px-8 py-3 bg-purple-600 text-white font-semibold rounded-lg shadow-md hover:bg-purple-700 transition duration-300 transform hover:scale-105"
        >
          Profile
        </button>
      </div>
    </div>
  );

  // Placeholder for the "Create Blog" form
  const CreateBlog = ({ onNavigate }) => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
      e.preventDefault();
      setLoading(true);
      try {
        await createBlog(title, content, token);
        setMessage('Blog created successfully!');
        setTitle('');
        setContent('');
        onNavigate('profile');
      } catch (error) {
        setMessage(`Error creating blog: ${error.message}`);
      } finally {
        setLoading(false);
      }
    };

    return (
      <div className="flex flex-col items-center p-8 bg-white rounded-lg shadow-2xl w-full max-w-xl mx-auto my-12 space-y-6">
        <h2 className="text-3xl font-bold text-gray-800 mb-4">Create New Blog</h2>
        <form onSubmit={handleSubmit} className="w-full space-y-4">
          <input
            type="text"
            placeholder="Blog Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            className="w-full px-4 py-3 rounded-md border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-500 transition duration-200"
          />
          <textarea
            placeholder="Blog Content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            required
            rows="8"
            className="w-full px-4 py-3 rounded-md border border-gray-300 focus:outline-none focus:ring-2 focus:ring-purple-500 transition duration-200"
          />
          <button
            type="submit"
            disabled={loading}
            className="w-full py-3 rounded-md text-white font-semibold transition-all duration-300 ease-in-out transform hover:scale-105"
            style={{ backgroundColor: loading ? '#6c757d' : '#9b59b6' }}
          >
            {loading ? 'Creating...' : 'Create Blog'}
          </button>
        </form>
        <MessageDisplay msg={message} type={message.includes('success') ? 'success' : 'error'} />
        <button onClick={() => onNavigate('profile')} className="mt-4 text-blue-500 hover:underline">
          Cancel
        </button>
      </div>
    );
  };

  // Placeholder for the "My Blogs" page
  const MyBlogs = ({ onNavigate }) => {
    const [blogs, setBlogs] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
      const fetchBlogs = async () => {
        try {
          if (!token) {
            setError("No token found. Please login.");
            setLoading(false);
            return;
          }
          const data = await getMyBlogs(token);
          setBlogs(data);
        } catch (error) {
          setError(error.message || 'Failed to fetch blogs');
        } finally {
          setLoading(false);
        }
      };
      fetchBlogs();
    }, [token]);

    return (
      <div className="flex flex-col items-center p-8 bg-white rounded-lg shadow-2xl w-full max-w-2xl mx-auto my-12 space-y-6">
        <h2 className="text-3xl font-bold text-gray-800 mb-4">My Blogs</h2>
        {loading && <p>Loading blogs...</p>}
        {error && <p className="text-red-500">{error}</p>}
        <div className="w-full space-y-4">
          {blogs.length > 0 ? (
            blogs.map((blog) => (
              <div key={blog.id} className="p-4 bg-gray-100 rounded-lg shadow-sm">
                <h3 className="text-xl font-semibold text-gray-800">{blog.title}</h3>
                <p className="text-gray-600 mt-2 line-clamp-2">{blog.content}</p>
              </div>
            ))
          ) : (
            !loading && <p className="text-gray-500">You haven't created any blogs yet.</p>
          )}
        </div>
        <button onClick={() => onNavigate('profile')} className="mt-4 px-6 py-3 bg-blue-500 text-white rounded-lg shadow-md hover:bg-blue-600 transition duration-300">
          Back to Profile
        </button>
      </div>
    );
  };

  // This function conditionally renders the correct component based on the `currentPage` state
  const renderPage = () => {
    switch (currentPage) {
      case 'login':
        return <Login onLoginSuccess={() => setCurrentPage('profile')} />;
      case 'profile':
        return <Profile
          onLogout={() => {
            setCurrentPage('home');
            setToken(null);
          }}
          onCreateBlog={() => setCurrentPage('create-blog')}
          onMyBlogs={() => setCurrentPage('my-blogs')}
          onHome={() => setCurrentPage('home')}
        />;
      case 'create-blog':
        return <CreateBlog onNavigate={setCurrentPage} />;
      case 'my-blogs':
        return <MyBlogs onNavigate={setCurrentPage} />;
      default:
        return <Home onNavigate={setCurrentPage} />;
    }
  };

  return (
    <div className="bg-gray-50 min-h-screen font-sans">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {renderPage()}
      </div>
    </div>
  );
};

export default App;
