// src/pages/CreateBlog.js
// This page provides a form to create a new blog post.

import React, { useState } from 'react';
import apiService from '../services/apiService';

const CreateBlog = ({ navigate, token }) => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      await apiService.createBlog(token, title, content);
      setMessage('Blog post created successfully!');
      setTitle('');
      setContent('');
      setTimeout(() => navigate('/profile'), 1500);
    } catch (error) {
      setMessage(error.message || 'Failed to create blog post.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white dark:bg-gray-800 rounded-lg shadow-lg mt-10">
      <h2 className="text-2xl font-bold mb-4 text-center">Create a New Blog Post</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          placeholder="Blog Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
          disabled={loading}
          className="w-full p-3 rounded-md border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <textarea
          placeholder="Blog Content"
          rows="10"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          required
          disabled={loading}
          className="w-full p-3 rounded-md border border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          type="submit"
          disabled={loading}
          className={`w-full p-3 rounded-md font-semibold transition-colors duration-200 ${loading ? 'bg-gray-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700 text-white'}`}
        >
          {loading ? 'Publishing...' : 'Publish Blog'}
        </button>
      </form>
      {message && <p className={`mt-4 text-center ${message.includes('successful') ? 'text-green-500' : 'text-red-500'}`}>{message}</p>}
    </div>
  );
};

export default CreateBlog;
